import PropTypes from "prop-types"
import Button from "./Button"

const NavBar = ({ title }) => {
  return (
    <nav className = "nav">
      <a href = "/" className = "site_title"> RateMyTrip</a>
      <ul>
       <CustomLink href = "/search">Search</CustomLink>
       <CustomLink href = "/explore">Explore</CustomLink>
       <CustomLink href = "/signIn">Sign In</CustomLink>
       <CustomLink href = "/signUp">Sign Up</CustomLink>

      </ul>
    </nav>

    // <div>
    //   <h1>
    //     {title}
    //     <Button text="Search"/>
    //     <Button text="Explore"/>
    //     <Button text="Post"/>
    //     <Button color="grey" text="Sign In"/>
    //     <Button color="grey" text="Sign Out"/>
    //   </h1>
    // </div>
  )
}

function CustomLink({href, children, ...props}) {
  const path = window.location.pathname

    return (
      <li className = {path === href ? "active" : ""}>
        <a href = {href} {...props}> {children}</a>
      </li>
    )
  }




NavBar.defaultProps = {
  title: "Navigation Bar"
}

NavBar.propTypes = {
  title: PropTypes.string,
}

export default NavBar
